mapper.select:
select
  id, order_id, goods_id, price, count, total_price, receive
from mall_order_goods


        and id = #{id}


        and order_id = #{orderId}


        and goods_id = #{goodsId}


        and price = #{price}


        and count = #{count}


        and total_price = #{totalPrice}


        and receive = #{receive}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mall_order_goods


        and id = #{id}


        and order_id = #{orderId}


        and goods_id = #{goodsId}


        and price = #{price}


        and count = #{count}


        and total_price = #{totalPrice}


        and receive = #{receive}
