mapper.select:
select
  address_id, user_id, province_code, city_code, region_code, community_id, nickname, telephone, complete_address, active, create_time, create_by, update_time, update_by, remark
from mall_user_address


        and address_id = #{addressId}


        and user_id = #{userId}


        and province_code = #{provinceCode}


        and city_code = #{cityCode}


        and region_code = #{regionCode}


        and community_id = #{communityId}


        and nickname = #{nickname}


        and telephone = #{telephone}


        and complete_address = #{completeAddress}


        and active = #{active}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mall_user_address


        and address_id = #{addressId}


        and user_id = #{userId}


        and province_code = #{provinceCode}


        and city_code = #{cityCode}


        and region_code = #{regionCode}


        and community_id = #{communityId}


        and nickname = #{nickname}


        and telephone = #{telephone}


        and complete_address = #{completeAddress}


        and active = #{active}


        and create_time = #{createTime}


        and create_by = #{createBy}


        and update_time = #{updateTime}


        and update_by = #{updateBy}


        and remark = #{remark}
