mapper.select:
select
  cart_id, user_id, goods_id, count, active
from mall_user_cart


        and cart_id = #{cartId}


        and user_id = #{userId}


        and goods_id = #{goodsId}


        and count = #{count}


        and active = #{active}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from mall_user_cart


        and cart_id = #{cartId}


        and user_id = #{userId}


        and goods_id = #{goodsId}


        and count = #{count}


        and active = #{active}
